// n8n JavaScript Code Node - Extract calendar data from chat message
// Parses natural language messages and formats them for Google Calendar

// Get the input data
const inputData = $input.all();
const outputData = [];

// Helper function to parse natural language date/time
function parseDateTime(message, baseDate = new Date()) {
  const today = new Date();
  const currentYear = today.getFullYear();

  // Extract time patterns (2pm, 14:00, etc.)
  const timeRegex = /(\d{1,2}):?(\d{2})?\s*(am|pm)?/gi;
  const timeMatch = message.match(timeRegex);

  // Extract date patterns (10 june 2025, june 10, etc.)
  const dateRegex = /(\d{1,2})\s+(january|february|march|april|may|june|july|august|september|october|november|december)\s*(\d{4})?/gi;
  const dateMatch = message.match(dateRegex);

  let eventDate = new Date();
  let eventTime = { hour: 14, minute: 0 }; // Default 2pm

  // Parse date
  if (dateMatch && dateMatch[0]) {
    const dateParts = dateMatch[0].match(/(\d{1,2})\s+(\w+)\s*(\d{4})?/i);
    if (dateParts) {
      const day = parseInt(dateParts[1]);
      const month = getMonthNumber(dateParts[2]);
      const year = dateParts[3] ? parseInt(dateParts[3]) : currentYear;
      eventDate = new Date(year, month, day);
    }
  }

  // Parse time
  if (timeMatch && timeMatch[0]) {
    const timeParts = timeMatch[0].match(/(\d{1,2}):?(\d{2})?\s*(am|pm)?/i);
    if (timeParts) {
      let hour = parseInt(timeParts[1]);
      const minute = timeParts[2] ? parseInt(timeParts[2]) : 0;
      const period = timeParts[3] ? timeParts[3].toLowerCase() : '';

      // Convert to 24-hour format
      if (period === 'pm' && hour !== 12) hour += 12;
      if (period === 'am' && hour === 12) hour = 0;

      eventTime = { hour, minute };
    }
  }

  // Combine date and time
  eventDate.setHours(eventTime.hour, eventTime.minute, 0, 0);
  return eventDate.toISOString();
}

// Helper function to convert month name to number
function getMonthNumber(monthName) {
  const months = {
    january: 0, february: 1, march: 2, april: 3, may: 4, june: 5,
    july: 6, august: 7, september: 8, october: 9, november: 10, december: 11
  };
  return months[monthName.toLowerCase()] || 0;
}

// Helper function to extract task name (first part)
function extractTaskName(message) {
  // Remove common prefixes
  let cleanMessage = message.replace(/^(add a reminder|remind me|schedule|create event)/i, '').trim();

  // Split by comma to get first part (task name)
  const parts = cleanMessage.split(',');
  if (parts.length > 0) {
    // Remove time/date info from first part
    const timePattern = /(\d{1,2}:?\d{0,2}\s*(am|pm)?|\d{1,2}\s+(january|february|march|april|may|june|july|august|september|october|november|december))/i;
    const taskName = parts[0].replace(timePattern, '').trim();
    return taskName || 'Task Reminder';
  }

  return 'Task Reminder';
}

// Helper function to extract task notes (second part)
function extractTaskNotes(message) {
  // Split by comma to get parts
  const parts = message.split(',');

  if (parts.length > 1) {
    // Get second part as task notes, clean up time/date info
    const timePattern = /(\d{1,2}:?\d{0,2}\s*(am|pm)?|\d{1,2}\s+(january|february|march|april|may|june|july|august|september|october|november|december))/gi;
    const taskNotes = parts[1].replace(timePattern, '').trim();
    return taskNotes || '';
  }

  return '';
}

// Process each input item
for (const item of inputData) {
  const data = item.json;

  // Extract the chat message
  const message = data.body?.message || '';
  const sender = data.body?.sender || 'Unknown';
  const timestamp = data.body?.timestamp || new Date().toISOString();

  if (message && message.toLowerCase().includes('reminder')) {
    // Parse the natural language message
    const taskName = extractTaskName(message);
    const taskNotes = extractTaskNotes(message);
    const startDateTime = parseDateTime(message);

    // Create end time (1 hour after start)
    const endDateTime = new Date(startDateTime);
    endDateTime.setHours(endDateTime.getHours() + 1);

    // Format for Google Calendar node
    const calendarEvent = {
      // Required fields
      summary: taskName,

      // Start and end times
      start: {
        dateTime: startDateTime,
        timeZone: 'Asia/Kolkata' // Adjust based on your timezone
      },

      end: {
        dateTime: endDateTime.toISOString(),
        timeZone: 'Asia/Kolkata'
      },

      // Optional fields
      description: ` ${taskNotes}\n\nCreated by: ${sender}\nOriginal timestamp: ${timestamp}`,

      // Set reminder (15 minutes before)
      reminders: {
        useDefault: false,
        overrides: [
          {
            method: 'popup',
            minutes: 15
          }
        ]
      },

      // Event status
      status: 'confirmed'
    };

    outputData.push({
      json: calendarEvent
    });
  }
}

// Return the formatted data for Google Calendar node
return outputData;

