// ‚úÖ Get email from webhook (passed through Google Sheets node)
const email = items[0].json.email || items[0].json.clientEmail;

// ‚úÖ Get rows from Google Sheet data (all items are sheet rows)
const rows = items.map(item => {
  // Extract only the sheet data, excluding the email field
  const { email, clientEmail, ...sheetData } = item.json;
  return sheetData;
}).filter(row => Object.keys(row).length > 0); // Remove empty rows

if (!rows.length) {
  throw new Error("No data found in Google Sheet.");
}

// ‚úÖ Convert rows to CSV
const headers = Object.keys(rows[0]);
const csvRows = [
  headers.join(","),
  ...rows.map(row =>
    headers.map(h => `"${(row[h] || "").toString().replace(/"/g, '""')}"`).join(",")
  )
];
const csv = csvRows.join("\n");

// ‚úÖ Return email + CSV as binary file + separate email output
return [
  {
    json: {
      email: email,
      fileName: "customer_report.csv",
      mimeType: "text/csv",
      clientEmail: email,  // ‚úÖ Added: Separate email field for next node
      csvRowCount: rows.length,
      generatedAt: new Date().toISOString()
    },
    binary: {
      data: {
        data: Buffer.from(csv).toString("base64"),
        mimeType: "text/csv",
        fileName: "customer_report.csv"
