// N8N JavaScript Code to transform webhook data for Google Sheets
// This code should be placed in a "Code" node in n8n

// Get the input data
const inputData = $input.all();

// Function to parse relative dates and convert to Google Sheets date format
function parseRelativeDate(text, baseDate = new Date()) {
  const today = new Date(baseDate);
  today.setHours(0, 0, 0, 0); // Reset time to start of day

  const lowerText = text.toLowerCase().trim();

  // Define relative date patterns
  const patterns = [
    // Tomorrow variations
    { 
      regex: /\b(tomorrow|tomm?orrow|tom+orrow|tmrw|2morrow)\b/i, 
      days: 1 
    },
    // Day after tomorrow variations
    { 
      regex: /\b(day after tom+orrow|day after tomorrow|overmorrow|2 days? from now)\b/i, 
      days: 2 
    },
    // Today variations
    { 
      regex: /\b(today|this day)\b/i, 
      days: 0 
    },
    // Yesterday variations
    { 
      regex: /\b(yesterday|last day)\b/i, 
      days: -1 
    },
    // Next week
    { 
      regex: /\b(next week)\b/i, 
      days: 7 
    },
    // Specific day patterns (next Monday, etc.)
    { 
      regex: /\b(next\s+)?(monday|mon)\b/i, 
      getDate: () => getNextWeekday(today, 1) 
    },
    { 
      regex: /\b(next\s+)?(tuesday|tue)\b/i, 
      getDate: () => getNextWeekday(today, 2) 
    },
    { 
      regex: /\b(next\s+)?(wednesday|wed)\b/i, 
      getDate: () => getNextWeekday(today, 3) 
    },
    { 
      regex: /\b(next\s+)?(thursday|thu)\b/i, 
      getDate: () => getNextWeekday(today, 4) 
    },
    { 
      regex: /\b(next\s+)?(friday|fri)\b/i, 
      getDate: () => getNextWeekday(today, 5) 
    },
    { 
      regex: /\b(next\s+)?(saturday|sat)\b/i, 
      getDate: () => getNextWeekday(today, 6) 
    },
    { 
      regex: /\b(next\s+)?(sunday|sun)\b/i, 
      getDate: () => getNextWeekday(today, 0) 
    },
    // Number of days patterns
    { 
      regex: /\b(\d+)\s+days?\s+from\s+now\b/i, 
      getDays: (match) => parseInt(match[1]) 
    },
    { 
      regex: /\bin\s+(\d+)\s+days?\b/i, 
      getDays: (match) => parseInt(match[1]) 
    }
  ];

  // Helper function to get next occurrence of a weekday
  function getNextWeekday(date, targetDay) {
    const currentDay = date.getDay();
    let daysToAdd = targetDay - currentDay;
    if (daysToAdd <= 0) {
      daysToAdd += 7; // Next week
    }
    const nextDate = new Date(date);
    nextDate.setDate(date.getDate() + daysToAdd);
    return nextDate;
  }

  // Check each pattern
  for (const pattern of patterns) {
    const match = lowerText.match(pattern.regex);
    if (match) {
      let targetDate;

      if (pattern.getDate) {
        targetDate = pattern.getDate();
      } else if (pattern.getDays) {
        const days = pattern.getDays(match);
        targetDate = new Date(today);
        targetDate.setDate(today.getDate() + days);
      } else if (pattern.days !== undefined) {
        targetDate = new Date(today);
        targetDate.setDate(today.getDate() + pattern.days);
      }

      if (targetDate) {
        // ADD ONE EXTRA DAY HERE
        targetDate.setDate(targetDate.getDate() + 1);

        // Format for Google Sheets (YYYY-MM-DD)
        return targetDate.toISOString().split('T')[0];
      }
    }
  }

  // Try to parse as regular date
  const parsedDate = new Date(text);
  if (!isNaN(parsedDate.getTime())) {
    // ADD ONE EXTRA DAY FOR REGULAR DATES TOO
    parsedDate.setDate(parsedDate.getDate() + 1);
    return parsedDate.toISOString().split('T')[0];
  }

  // If no date found, default to today + 1
  const defaultDate = new Date(today);
  defaultDate.setDate(today.getDate() + 1);
  return defaultDate.toISOString().split('T')[0];
}

// Function to extract task and date from message
function extractTaskAndDate(message) {
  let task = message;
  let dateText = 'today'; // default

  // Define all date keywords
  const dateWords = ['tomorrow', 'today', 'yesterday', 'next week', 'monday', 'tuesday', 'wednesday', 'thursday', 'friday', 'saturday', 'sunday', 'tomm', 'tommorrow', 'tmrw', '2morrow', 'day after tomorrow', 'overmorrow'];

  // Step 1: Remove "remind me" from the beginning
  task = task.replace(/^remind me\s*/i, '').trim();

  // Step 2: Check if it starts with "to" and remove it
  task = task.replace(/^to\s+/i, '').trim();

  // Step 3: Find date in the message
  const lowerTask = task.toLowerCase();
  let foundDate = null;

  // Look for date words in the task
  for (const dateWord of dateWords) {
    const regex = new RegExp('\\b' + dateWord + '\\b', 'i');
    if (regex.test(lowerTask)) {
      foundDate = dateWord;
      dateText = dateWord;
      break;
    }
  }

  // Step 4: Remove the date from the task
  if (foundDate) {
    const dateRegex = new RegExp('\\b' + foundDate + '\\b', 'gi');
    task = task.replace(dateRegex, '').trim();
  }

  // Step 5: Clean up extra spaces and "to" words
  task = task.replace(/\s+/g, ' ').trim();
  task = task.replace(/^to\s+/i, '').trim(); // Remove any remaining "to" at the start

  return { task, dateText };
}

