      }
    },
    debug: {
      sampleRow: rows[0] || null,
      fieldNames: rows[0] ? Object.keys(rows[0]) : []
    }
  },
  binary: {
    data: {
      data: Buffer.from(csv).toString("base64"),
      mimeType: "text/csv",
      fileName: `project_report_${now.toISOString().split('T')[0]}.csv`
    }
  }
}];

const today = new Date();
const todayMonthDay = `${today.getMonth() + 1}-${today.getDate()}`;

return items.filter(item => {
  const data = item.json;

  if (!data["Date"]) return false;

  const eventDate = new Date(data["Date"]);
  const eventMonthDay = `${eventDate.getMonth() + 1}-${eventDate.getDate()}`;

  return eventMonthDay === todayMonthDay;
});

const now = new Date();

return items.filter(item => {
  const start = new Date(item.json.start.dateTime || item.json.start.date);  // Handles all-day events too
  const end = new Date(item.json.end.dateTime || item.json.end.date);
  return start <= now && now <= end;
});
